import React from "react";
import {
   Box,
   Typography,
   Grid,
   Card,
   CardContent,
   Button,
   Chip,
   List,
   ListItem,
   ListItemText,
   ListItemSecondaryAction,
   IconButton,
   Paper,
   Avatar,
} from "@mui/material";
import { Add, Edit, Delete, Phone, Email, Event, People, AccessTime } from "@mui/icons-material";

const ReservationsPage: React.FC = () => {
   // Mock data para demonstra√ß√£o
   const reservations = [
      {
         id: "1",
         customerName: "Carlos Silva",
         customerPhone: "(11) 99999-1111",
         customerEmail: "carlos@email.com",
         date: "2025-08-03",
         time: "19:30",
         guests: 4,
         status: "CONFIRMADA",
         table: { number: 5 },
         notes: "Anivers√°rio - precisa de bolo",
      },
      {
         id: "2",
         customerName: "Ana Santos",
         customerPhone: "(11) 88888-2222",
         customerEmail: "ana@email.com",
         date: "2025-08-03",
         time: "20:00",
         guests: 2,
         status: "CONFIRMADA",
         table: { number: 2 },
         notes: null,
      },
      {
         id: "3",
         customerName: "Roberto Costa",
         customerPhone: "(11) 77777-3333",
         customerEmail: "roberto@email.com",
         date: "2025-08-04",
         time: "19:00",
         guests: 6,
         status: "CONFIRMADA",
         table: { number: 8 },
         notes: "Jantar de neg√≥cios",
      },
   ];

   const getStatusColor = (status: string) => {
      switch (status) {
         case "CONFIRMADA":
            return "success";
         case "CANCELADA":
            return "error";
         case "FINALIZADA":
            return "default";
         default:
            return "default";
      }
   };

   const getStatusText = (status: string) => {
      switch (status) {
         case "CONFIRMADA":
            return "Confirmada";
         case "CANCELADA":
            return "Cancelada";
         case "FINALIZADA":
            return "Finalizada";
         default:
            return status;
      }
   };

   const formatDate = (dateString: string) => {
      const date = new Date(dateString + "T00:00:00");
      return date.toLocaleDateString("pt-BR", {
         day: "2-digit",
         month: "2-digit",
         year: "numeric",
      });
   };

   const isToday = (dateString: string) => {
      const today = new Date().toISOString().split("T")[0];
      return dateString === today;
   };

   const todayReservations = reservations.filter((r) => isToday(r.date));
   const upcomingReservations = reservations.filter((r) => !isToday(r.date));

   return (
      <Box>
         <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
            <Box>
               <Typography variant="h4" gutterBottom fontWeight="bold">
                  Reservas
               </Typography>
               <Typography variant="subtitle1" color="text.secondary">
                  Gerencie as reservas do restaurante
               </Typography>
            </Box>

            <Button variant="contained" startIcon={<Add />}>
               Nova Reserva
            </Button>
         </Box>

         {/* Estat√≠sticas */}
         <Grid container spacing={2} sx={{ mb: 4 }}>
            <Grid item xs={12} sm={4}>
               <Paper sx={{ p: 2, textAlign: "center" }}>
                  <Typography variant="h4" color="primary" fontWeight="bold">
                     {todayReservations.length}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                     Reservas Hoje
                  </Typography>
               </Paper>
            </Grid>
            <Grid item xs={12} sm={4}>
               <Paper sx={{ p: 2, textAlign: "center" }}>
